sys_variable_value.value:
(function execute(inputs, outputs) {
	inputs = new FirstAdvantageSpokeUtils().trimStringInputs(inputs);
	outputs.candidate_id = new FirstAdvantageSpokeUtils().validateCandidate(
		inputs.candidate_id);
})(inputs, outputs);

