sys_variable_value.value:
(function execute(inputs, outputs) {
	inputs = new FirstAdvantageSpokeUtils().trimStringInputs(inputs);
	if (inputs.status_code == '200') {
		outputs.status = 'Success';
		outputs.candidate_details = JSON.parse(inputs.response);
	} else
		outputs = new FirstAdvantageSpokeErrorHandler()
		.getCandidateDetailsErrorHandler(inputs, outputs);
})(inputs, outputs);

