sys_variable_value.value:
(function execute(inputs, outputs) {
	inputs = new FirstAdvantageSpokeUtils().trimStringInputs(inputs);
	if (inputs.status_code == '200') {
        outputs.status = 'Success';
		var response = JSON.parse(inputs.response);
		outputs.link = response.order.application_details.application_url;
	} else
		outputs = new FirstAdvantageSpokeErrorHandler()
		.getInviteViewLinkByApplicationIdErrorHandler(inputs, outputs);
})(inputs, outputs);

