sys_variable_value.value:
(function execute(inputs, outputs) {
    inputs = new FirstAdvantageSpokeUtils().trimStringInputs(inputs);
    if (inputs.status_code == '202') {
        outputs.status = 'Success';
    } else
        outputs = new FirstAdvantageSpokeErrorHandler()
        .updateUserPasswordErrorHandler(inputs, outputs);
})(inputs, outputs);

