sys_variable_value.value:
(function execute(inputs, outputs) {
    inputs = new FirstAdvantageSpokeUtils().trimStringInputs(inputs);
    if (inputs['user_id'] == '' || inputs['user_id'] == null)
        new FirstAdvantageSpokeUtils().activateDeactivateUser("User ID");
    outputs.user_id = inputs.user_id;
})(inputs, outputs);

