sys_variable_value.value:
(function execute(inputs, outputs) {
	inputs = new FirstAdvantageSpokeUtils().trimStringInputs(inputs);
	var json_data = {};
	for (var key in inputs) {
		if (inputs[key]) {
			if (key == "client_references" || key == "user_defined_fields")
				json_data[key] = new FirstAdvantageSpokeUtils()
				.buildArrayPayload(key, inputs[key]);
			else if (key == "order_as") {
				if (inputs.order_as.user_id || inputs.order_as.account_id ||
					inputs.order_as.email) {
					if ((inputs.order_as.account_id && inputs.order_as
							.user_id && inputs.order_as.email)) {
						json_data["additional_attributes"] = {
							"order_as": inputs[key]
						};
					} else
						throw new FirstAdvantageSpokeUtils()
							.validateOrderAs();
				}
			} else if (key == "position_locations" || key ==
				"quote_backs") {
				json_data[key] = new FirstAdvantageSpokeUtils()
					.buildArrayOfObjectsPayload(key, inputs[key]);
			}
			else
				json_data[key] = inputs[key] ? inputs[key] : undefined;
		}
	}
	outputs.payload = JSON.stringify(json_data);
})(inputs, outputs);

