sys_variable_value.value:
(function execute(inputs, outputs) {
	inputs = new FirstAdvantageSpokeUtils().trimStringInputs(inputs);
	if (inputs.status_code == '201') {
		outputs.status = 'Success';
		var response = JSON.parse(inputs.response);
		outputs.candidate_id = response.candidate_id;
	} else if ((inputs.status_code == '409') && (inputs.response.indexOf(
			'Candidate data conflict.') != -1)) {
		outputs.status = 'Success';
		var response = (JSON.parse(inputs.response)).message;
		var id = (response.split(" "));
		var candidate_id = id[1].split("=");
		outputs.candidate_id = candidate_id[1];
	} else
		outputs = new FirstAdvantageSpokeErrorHandler()
		.createNewCandidateErrorHandler(inputs, outputs);
})(inputs, outputs);

