sys_variable_value.value:
(function execute(inputs, outputs) {
	inputs = new FirstAdvantageSpokeUtils().trimStringInputs(inputs);
	var given_name = new FirstAdvantageSpokeUtils().validateFirstName(inputs
		.given_name);
	var family_name = new FirstAdvantageSpokeUtils().validateLastName(inputs
		.family_name);
	var email = new FirstAdvantageSpokeUtils().validateEmail(inputs.email);
	var payload = {};
	var persons = [];
	var person = {};
	for (var key in inputs) {
		if (inputs[key]) {
			if ((key != "email") && (key != "phones") && (key !=
					"addresses")) {
				person[key] = inputs[key] ? inputs[key] : undefined;
			}
		}
	}
	person['type'] = "subject",
		persons.push(person);
	payload['persons'] = persons;
	payload['email'] = email;
	payload['phones'] = new FirstAdvantageSpokeUtils()
		.buildArrayOfObjectsPayload('phones', inputs.phones);
	payload['addresses'] = new FirstAdvantageSpokeUtils()
		.buildArrayOfObjectsPayload('addresses', inputs.addresses);
	outputs.payload = JSON.stringify(payload);
})(inputs, outputs);

